def filter_bank(wave):
    assert wave in _wavelets_filter_bank
    (L, H, _, _) = _wavelets_filter_bank[wave]
    
    L = np.array(L)[:, None]
    H = np.array(H)[:, None]
    
    LL = L.dot(L.T)
    LH = L.dot(H.T)
    HL = H.dot(L.T)
    HH = H.dot(H.T)
    
    return LL, LH, HL, HH


